function Todo({n}) {
	const description = `Item ${n}`
	const editInputId = `edit-todo-input-${n}`

	return m("li.completed.editing", [
		m("div.view", [
			m("input[type=checkbox].toggle", {checked: true}),
			m("label", description),
			m("button.destroy", "Delete"),
		]),
		m("div.input-container", [
			m("input.edit", {
				id: editInputId,
				value: description,
			}),
			m("label.visually-hidden", {
				for: editInputId,
			}, "Edit todo input")
		]),
	])
}

let ticks = 0

m.mount(document.body, () => [
	m("button", {onclick() { ticks++ }}, "Add"),
	m("div", "Ticks: ", ticks),
	m("ul", Array.from({length: ticks}, (_, i) => m(Todo, {n: i + 1}))),
])
