const root = document.createElement("ul")
const button = document.createElement("button")
const ticksDisplay = document.createTextNode("0")

let ticks = 0

button.onclick = () => {
	const n = ++ticks
	const description = `Item ${n}`
	const editInputId = `edit-todo-input-${n}`
	ticksDisplay.nodeValue = n
	const li = document.createElement("li")
	let parent = document.createElement("div")
	parent.className = "view"
	let child = document.createElement("input")
	child.className = "toggle"
	child.type = "checkbox"
	child.checked = true
	parent.append(child)
	child = document.createElement("label")
	child.append(document.createTextNode(description))
	parent.append(child)
	child = document.createElement("button")
	child.className = "destroy"
	child.append(document.createTextNode("Delete"))
	parent.append(child)
	li.append(parent)
	parent = document.createElement("div")
	parent.className = "input-container"
	child = document.createElement("input")
	child.value = description
	child.id = editInputId
	child.className = "edit"
	parent.append(child)
	child = document.createElement("label")
	child.className = "visually-hidden"
	child.htmlFor = editInputId
	child.append(document.createTextNode("Edit todo input"))
	parent.append(child)
	li.append(parent)
	root.append(li)
}

const ticksDiv = document.createElement("div")
ticksDiv.append(document.createTextNode("Ticks: "))
ticksDiv.append(ticksDisplay)
button.append(document.createTextNode("Add"))
document.body.append(button)
document.body.append(ticksDiv)
document.body.append(root)
